using namespace std;
const int P=998244353;
const int inv=P-P/2;
void FWT(int* a,const int n){
	for(int d = 1; d != 1 << n; d <<= 1)
    	for(int i = 0; i != 1 << n; i += d << 1)
        	for(int j = 0; j != d; ++j){
	        	int l=a[i+j],r=a[i+j+d];
	            a[i+j]=l+r>=P?l+r-P:l+r;
				a[i+j+d]=l-r<0?l-r+P:l-r;
			}
}
void UFWT(int* a,const int n){
	for(int d = 1; d != 1 << n; d <<= 1)
    	for(int i = 0; i != 1 << n; i += d << 1)
        	for(int j = 0; j != d; ++j){
	        	int l=a[i+j],r=a[i+j+d];
	            a[i+j]=l+r>=P?l+r-P:l+r;
				a[i+j+d]=l-r<0?l-r+P:l-r;
				a[i+j]=1ll*inv*a[i+j]%P;
				a[i+j+d]=1ll*inv*a[i+j+d]%P;
			}
}
const int N=1<<18;
int a[N],b[N];
int qpow(int a,int b=P-2){
	int res=1;
	for(;b;b>>=1){
		if(b&1){
			res=1ll*res*a%P;
		}
		a=1ll*a*a%P;
	}
	return res;
}
int main(){
	int n;
	cin>>n;
	int sum=0;
	for(int i=0;i<(1<<n);i++){
		cin>>a[i];
		sum=(sum+a[i])%P;
	}
	sum=qpow(sum);
	for(int i=0;i<(1<<n);i++){
		a[i]=1ll*sum*a[i]%P;
		b[i]=1;
	}
	b[0]=0;
	FWT(a,n);
	FWT(b,n);
	a[0]=0;
	for(int i=1;i<(1<<n);i++){
		a[i]=1ll*qpow((P+1-a[i])%P)*b[i]%P;
	}
	FWT(a,n);
	for(int i=0;i<(1<<n);i++){
		cout<<(P+a[i]-a[0])%P<<'\n';
	}
}

