int fastpow(int x,int y){
	int res=1;
	while(y){if(y&1)res=1ll*res*x%mod;x=1ll*x*x%mod;y>>=1;}
	return res;
}
void fwt(int *p,int op){
	for(int i=1;i<len;i<<=1)
		for(int j=0;j<len;j+=i<<1)
			for(int k=0;k<i;++k){
				int x=p[j+k],y=p[j+k+i];
				p[j+k]=(x+y)%mod;
				p[j+k+i]=(x-y+mod)%mod;
			}
	if(!op){
		int inv_len=fastpow(len,mod-2);
		for(int i=0;i<1<<n;++i)p[i]=1ll*p[i]*inv_len%mod;
	}
}
int main(){
