void inc(int &a,int b) { if((a+=b)>=MOD) a-=MOD; }
void dec(int &a,int b) { if((a-=b)<0) a+=MOD; }
int pw(int x,int n) { int ret=1; while(true) { if(n&1) ret=(ll)ret*x%MOD; if((n>>=1)==0) return ret; x=(ll)x*x%MOD; } }
int inv(int x) { assert(x!=0); return pw(x,MOD-2); }

void wht(int *a,int nbits) {
	REP(bit,nbits) for(int off=0;off<(1<<nbits);off+=2<<bit) REP(i,1<<bit) { int x=a[off+i],y=a[off+(1<<bit)+i]; a[off+i]=a[off+(1<<bit)+i]=x; inc(a[off+i],y); dec(a[off+(1<<bit)+i],y); }
}

int n;
int a[1<<MAXN];
int x[1<<MAXN];

int p[1<<MAXN];
int lhs[1<<MAXN];
int rhs[1<<MAXN];


void solve() {
	int asum=0; REP(i,1<<n) asum+=a[i];
	int pmlt=inv(asum); REP(i,1<<n) p[i]=(ll)a[i]*pmlt%MOD;
	REP(i,1<<n) lhs[i]=p[i]; lhs[0]=(lhs[0]+MOD-1)%MOD;
	REP(i,1<<n) rhs[i]=MOD-1; rhs[0]=(rhs[0]+(1<<n))%MOD;
	wht(lhs,n);
	wht(rhs,n);
	//printf("lhs:"); REP(i,1<<n) printf(" %d",lhs[i]); puts("");
	//printf("rhs:"); REP(i,1<<n) printf(" %d",rhs[i]); puts("");
