	wht(x,n);
	int offset=MOD-x[0]; REP(i,1<<n) inc(x[i],offset);
	int xmlt=inv(1<<n); REP(i,1<<n) x[i]=(ll)x[i]*xmlt%MOD;
	//printf("x:"); REP(i,1<<n) printf(" %d",x[i]); puts("");
}

