/*
f[i]: i -> 0
f[0] = 0
f[i] = 1 +  f[j] * p[i^j]
<f> * <p> = <?, f[i] - 1>

<p> = 1, so <f> = <?, f[i] - 1>
? = f[0] + 2^n - 1;

<f> * <p> = <f[0] + 2^n - 1, f[i] - 1>
<f> * <p[0] - 1, p[i]> = <2^n - 1, -1, -1, ...>

ifwt

square up : ? * 0 = 0
? = 0 / 0 is not feasible

but we know that f[0] = 0, so not to consider the issue, calculate a[0]' out
then modify <f> to eliminate the bias to make a[0] = 0
*/

#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

template <typename _tp> inline void read(_tp&x) {
	char ch = getchar(), sgn = 0; x = 0;
	while(ch != '-' and !isdigit(ch)) ch = getchar(); if(ch == '-') ch = getchar(), sgn = 1;
	while(isdigit(ch)) x = x * 10 + (ch ^ 48), ch = getchar(); if(sgn) x = -x;
}

const int mod = 998244353, inv2 = mod + 1 >> 1;

inline int qm(const int x) {return x < mod ? x : x - mod;}
inline int qpow(int A, int B) {
	int r = 1;
	for(; B; A = (ll)A * A%mod, B >>= 1)
		if(B&1) r = (ll)r * A%mod;
	return r;
}

void fwt(int*a, int n) {
	for(int i = 1; i < n; i <<= 1)
	for(int j = 0; j < n; j += (i << 1))
	for(int k = 0; k < i; ++ k) {
		const int x = a[j+k], y = a[j+k+i];
		a[j+k] = qm(x + y), a[j+k+i] = qm(x - y+mod);
	}
}

void ifwt(int*a, int n) {
	for(int i = 1; i < n; i <<= 1)
	for(int j = 0; j < n; j += (i << 1))
	for(int k = 0; k < i; ++ k) {
		const int x = a[j+k], y = a[j+k+i];
		a[j+k] = (ll)qm(x + y) * inv2%mod;
		a[j+k+i] = (ll)qm(x - y+mod) * inv2%mod;
	}
}

const int N = 1 << 18 | 3;
int p[N], f[N];
int n;

int main() {
	read(n), n = 1 << n;
	int sum = 0;
	for(int i = 0; i < n; ++ i) read(p[i]), sum += p[i];
	sum = qpow(sum, mod-2);
	for(int i = 0; i < n; ++ i) p[i] = (ll)p[i] * sum%mod;
	p[0] = qm(p[0] - 1+mod);

	for(int i = 1; i < n; ++ i) f[i] = mod - 1;
	f[0] = n - 1;

	fwt(f, n), fwt(p, n);
	for(int i = 0; i < n; ++ i) f[i] = (ll)f[i] * qpow(p[i], mod-2)%mod;
	ifwt(f, n);

	if(f[0]) {
		int bias = (ll)(mod - f[0]) * n%mod;
		fwt(f, n);
		for(int i = 0; i < n; ++ i)
			if(!p[i]) {
				f[i] = bias;
				break;
			}
		ifwt(f, n);
	}

	for(int i = 0; i < n; ++ i)
		printf("%d\n", f[i]);
	return 0;
}

