const int N = 18, S = 1 << N, Mod = 998244353;

inline int fpm(int x, int power) {
	int res = 1;
	for (; power; power >>= 1, x = 1ll * x * x % Mod)
		if (power & 1) res = 1ll * res * x % Mod;
	return res;
}

int n, len, p[S], s[S], sum, pre[S + 1], inv[S + 1];

void FWT(int *P, int opt) {
	for (int i = 2, p; p = i >> 1, i <= len; i <<= 1)
		for (int j = 0; j < len; j += i) Rep (k, p) {
			int u = P[j + k], v = P[j + k + p];
			P[j + k] = (u + v) % Mod; P[j + k + p] = (u + Mod - v) % Mod;
		}
	if (!~opt) {
		int invn = fpm(len, Mod - 2);
		Rep (i, len) P[i] = 1ll * P[i] * invn % Mod;
	}
}

using namespace pb_ds;

int main() {

	File();

	len = 1 << (n = read());
	Rep (i, len) (sum += p[i] = read()) %= Mod;
	sum = fpm(sum, Mod - 2);
	Rep (i, len) p[i] = 1ll * sum * p[i] % Mod; -- p[0];
	Rep (i, len) s[i] = (i ? 0 : len) - 1;

	FWT(p, 1); FWT(s, 1); 

	pre[0] = 1;
	For (i, 1, len)
		pre[i] = 1ll * max(1, p[i - 1]) * pre[i - 1] % Mod;
	inv[len] = fpm(pre[len], Mod - 2);
	Fordown (i, len - 1, 0)
		inv[i] = 1ll * inv[i + 1] * max(1, p[i]) % Mod;
	Rep (i, len) if (p[i])
		p[i] = 1ll * s[i] * inv[i + 1] % Mod * pre[i] % Mod; 

	FWT(p, -1);

	Rep (i, len) 
		Print((p[i] - p[0] + Mod) % Mod);
	io :: flush();

	return 0;

}

