using namespace std;
typedef long long ll;
ll mod = 998244353;
int LOG;
class Hadamard
{
public:
	ll d[2][1 << 19];
	vector<ll>conv(vector<ll>v)
	{
		for (int i = 0; i < v.size(); i++)d[0][i] = v[i];
		int cur = 0;
		for (int i = 0; i < LOG; i++)
		{
			for (int j = 0; j < (1 << LOG); j += (1 << (LOG - i)))
			{
				int s = 1 << (LOG - i - 1);
				for (int k = 0; k < s; k++)d[1 - cur][j + k] = (d[cur][j + k] + d[cur][j + s + k]) % mod, d[1 - cur][j + s + k] = d[cur][j + s + k];
			}
			cur = 1 - cur;
		}
		for (int i = LOG - 1; i >= 0; i--)
		{
			for (int j = 0; j < (1 << LOG); j += (1 << (LOG - i)))
			{
				int s = 1 << (LOG - i - 1);
				for (int k = 0; k < s; k++)d[1 - cur][j + k] = d[cur][j + k], d[1 - cur][j + s + k] = (d[cur][j + k] + mod + mod - d[cur][j + s + k] * 2) % mod;
			}
			cur = 1 - cur;
		}
		vector<ll>r;
		for (int i = 0; i < (1 << LOG); i++)r.push_back(d[cur][i]);
		return r;
	}
};
Hadamard had;
ll po(ll a, ll b)
{
	if (b == 0)return 1;
	ll z = po(a, b / 2);
	z = z*z%mod;
	if (b & 1)z = z*a%mod;
	return z;
}
ll inv(ll a)
{
	return po(a, mod - 2);
}
int main()
{
	int num;
	scanf("%d", &num);
	LOG = num;
	vector<ll>a;
	ll s = 0;
	for (int i = 0; i < (1 << num); i++)
	{
		int z;
		scanf("%d", &z);
		a.push_back(z);
		s += z;
	}
	ll iv = inv(s);
	for (int i = 0; i < (1 << num); i++)a[i] = a[i] * iv%mod;
	a = had.conv(a);
	for (int i = 1; i < (1 << num); i++)a[i] = inv(1 + mod - a[i]);
	a[0] = 0;
	a = had.conv(a);
	for (int i = 0; i < (1 << num); i++)printf("%lld\n", (a[0] + mod - a[i]) % mod);
}

