const int N = (1<<18)+5;
const int mo = 998244353;

LL quickmi(LL x,LL tim){
	LL ret=1;
	for(;tim;tim/=2,x=x*x%mo)
		if (tim&1)ret=ret*x%mo;
	return ret;
}

int n;
LL f[N],g[N],ans[N];

int main(){
	n=get();
	LL s=0;
	fo(i,0,(1<<n)-1)s+=(f[i]=get());
	LL NY=quickmi(s,mo-2);
	fo(i,0,(1<<n)-1)f[i]=f[i]*NY%mo;
	for(int i=1;i<(1<<n);i<<=1)
		for(int j=0;j<(1<<n);j+=i<<1)
			for(int k=0;k<i;k++){
				LL l=f[j+k],r=f[i+j+k];
				f[i+j+k]=l<r?l-r+mo:l-r;
				f[j+k]=l+r>=mo?l+r-mo:l+r;
			}
	LL all=1,v=0;
	fo(i,1,(1<<n)-1){
		v=(v*(mo+1-f[i])+all*f[i])%mo;
		all=all*(mo+1-f[i])%mo;
	}
	v=(mo-v)%mo;
	LL A=v;
	g[0]=v;
	fo(i,1,(1<<n)-1){
		g[i]=all*quickmi((mo+1-f[i])%mo,mo-2)%mo*f[0]%mo;
		g[i]=(mo-g[i])%mo;
		A=(A+g[i])%mo;
	}
	for(int i=1;i<(1<<n);i<<=1)
		for(int j=0;j<(1<<n);j+=i<<1)
			for(int k=0;k<i;k++){
				LL l=g[j+k],r=g[i+j+k];
				g[i+j+k]=l<r?l-r+mo:l-r;
				g[j+k]=l+r>=mo?l+r-mo:l+r;
			}
	LL na=quickmi(all,mo-2)%mo;
	fo(i,0,(1<<n)-1){
		LL B=g[i];
		ans[i]=B<A?B-A+mo:B-A;
		ans[i]=ans[i]*na%mo;
	}
	ans[0]=0;
	fo(i,0,(1<<n)-1)printf("%lld\n",ans[i]);
	return 0;
}

