void XORFFT(vector<int>& a, int n, int p, int invert) {
    for (int i = 1; i < n; i <<= 1) {
        for (int j = 0; j < n; j += i << 1) {
            for (int k = 0; k < i; k++) {
                int u = a[j + k], v = a[i + j + k];
                a[j + k] = u + v;
                if (a[j + k] >= p) a[j + k] -= p;
                a[i + j + k] = u - v;
                if (a[i + j + k] < 0) a[i + j + k] += p;
            }
        }
    }
    if (invert) {
        long long inv = fpow(n, p - 2, p);
        for (int i = 0; i < n; i++) a[i] = a[i] * inv % p;
    }
}

void chemthan() {
    int n; cin >> n;
    vi a(1 << n);
    int sum = 0;
    for (int& x : a) cin >> x, addmod(sum, x);
    db(sum) endln;
    for (int& x : a) x = mult(x, inv(sum));
    vi b(1 << n);
    FOR(i, 0, 1 << n) submod(b[i], 1);
    b[0] = fpow(2, n);
    submod(b[0], 1);
    XORFFT(a, 1 << n, MOD, 0);
    XORFFT(b, 1 << n, MOD, 0);
    FOR(i, 0, 1 << n) a[i] = mult(b[i], inv(a[i] - 1 + MOD));
    XORFFT(a, 1 << n, MOD, 1);
    FOR(i, 0, 1 << n) cout << (a[i] - a[0] + MOD) % MOD << "\n";
}

