int n, len;
LL p[MAXN], psum, a[MAXN];

inline LL ksm(LL x, LL k) {
	LL ret = 1;
	while (k) {
		if (k & 1) ret = ret * x % MOD;
		x = x * x % MOD;
		k >>= 1;
	}
	return ret;
}

void FWT(LL *a) {
	for (int i=1;i<len;i<<=1) {
		int p = i << 1;
		for (int j=0;j<len;j+=p) {
			for (int k=0;k<i;++k) {
				LL x = a[j+k], y = a[j+k+i];
				a[j+k] = (x + y) % MOD;
				a[j+k+i] = (x - y + MOD) % MOD;
			}
		}
	}
}

void IFWT(LL *a) {
	for (int i=1;i<len;i<<=1) {
		int p = i << 1;
		for (int j=0;j<len;j+=p) {
			for (int k=0;k<i;++k) {
				LL x = a[j+k], y = a[j+k+i];
				a[j+k] = (x + y) * INV2 % MOD;
				a[j+k+i] = (x - y + MOD) * INV2 % MOD;
			}
		}
	}
}

void init() {
	read(n); len = 1 << n;
	for (int i=0;i<len;++i) {
		read(p[i]);
		psum += p[i];
	}
	psum = ksm(psum, MOD - 2);
	for (int i=0;i<len;++i) {
		p[i] = p[i] * psum % MOD;
		a[i] = MOD - 1;
	}
	p[0] = (p[0] - 1 + MOD) % MOD;
	a[0] = len - 1;
}

void solve() {
	FWT(a); FWT(p);
	for (int i=0;i<len;++i) {
		a[i] = a[i] * ksm(p[i], MOD - 2) % MOD;
	}
	IFWT(a);
	for (int i=0;i<len;++i) {
		printf("%lld\n", (a[i] - a[0] + MOD) % MOD);
	}
}

int main() {
	init();
	solve();
	return 0;
}

