    a = a * a % P;
    w/=2;
  }
  return r;
}
int mul(int a,int b){
  return a * b % P;
}
void add(int &a,int b){
  a += b;
  if(a >= P)
    a -= P;
}
void sub(int &a,int b){
  a -= b;
  if(a < 0)
    a += P;
}
vector<int> licz(vector<int> a, vector<int> b, int n){
