    add(a1[i], a[i*2+1]);
    b1[i] = b[i*2];
    add(b1[i], b[i*2+1]);
  }
  vector<int> r1 = licz(a1,b1,n/2);
  vector<int> a2(n/2),b2(n/2);
  R(i,n/2){
    a2[i] = a[i*2];
