inline int qkpow ( int a, int b, const int p = MOD ) {
	int ret = 1;
	for ( ; b; a = 1LL * a * a % p, b >>= 1 ) ret = 1LL * ret * ( b & 1 ? a : 1 ) % p;
	return ret;
}

inline void FWT_XOR ( int* A, const int n, const int tp ) {
	for ( Int i = 2, stp = 1; i <= n; i <<= 1, stp <<= 1 ) {
		for ( Int j = 0; j < n; j += i ) {
			for ( Int k = j, t; k < j + stp; ++ k ) {
				t = A[k];
				A[k] = ( A[k] + A[k + stp] ) % MOD;
				A[k + stp] = ( ( t - A[k + stp] ) % MOD + MOD ) % MOD;
				if ( ! ~ tp ) {
					A[k] = 1LL * A[k] * Inv2 % MOD;
					A[k + stp] = 1LL * A[k + stp] * Inv2 % MOD;
				}
			}
		}
	}
}

