ll inq(ll x, ll y)
{
    if (!y) return 1;
    ll l = inq(x, y / 2);
    if (y % 2) return l * l % MOD * x % MOD;
    return l * l % MOD;
}

vector<int> solve(vector<int> b, vector<int> z)
{
    int n = b.size();
    if (n == 1)
    {
        if (b[0] == 0 && z[0] == 0)
        {
            return {1};
        }
        return {z[0] * inq(b[0], MOD - 2) % MOD};
    }
    vector<int> b0(n / 2), b1(n / 2), z0(n / 2), z1(n / 2);
    for (int i = 0; i < n / 2; i++)
    {
        b0[i] = (b[2 * i] + b[2 * i + 1]) % MOD;
        z0[i] = (z[2 * i] + z[2 * i + 1]) % MOD;
        b1[i] = (b[2 * i] - b[2 * i + 1] + MOD) % MOD;
        z1[i] = (z[2 * i] - z[2 * i + 1] + MOD) % MOD;
    }
    vector<int> x0 = solve(b0, z0), x1 = solve(b1, z1);
    vector<int> x(n);
    for (int i = 0; i < n / 2; i++)
    {
        x[2 * i] = (x0[i] + x1[i]) * rev2 % MOD;
        x[2 * i + 1] = (x0[i] + MOD - x1[i]) * rev2 % MOD;
    }
    return x;
}

