constexpr int Add(int a, int b)
{
	return a + b >= MOD ? a + b - MOD : a + b;
}

constexpr int Sub(int a, int b)
{
	return a - b < 0 ? a - b + MOD : a - b;
}

constexpr int Mult(int a, int b)
{
	return a * 1LL * b % MOD;
}

constexpr int squ(int x)
{
	return Mult(x, x);
}

void exgcd(int a, int b, int &x, int &y)
{
	if (b == 0)
		x = 1, y = 0;
	else
		exgcd(b, a % b, y, x), y -= x * (a / b);
}

inline int inverse(int a)
{
	int invx, invy;
	exgcd(a, MOD, invx, invy);
	return (invx % MOD + MOD) % MOD;
}

int N, A[1 << Max_N], E[1 << Max_N], SumA;

void FWT(int F[1 << Max_N], bool inv = false)
{
	for (int i = 0;i <= N - 1;++i)
		for (int S = 0, x, y;S < (1 << N);++S)
			if ((S & (1 << i)) == 0)
			{
				x = F[S], y = F[S + (1 << i)];
				F[S] = Add(x, y), F[S + (1 << i)] = Sub(x, y);
			}
	if (inv)
		for (int S = 0, invL = inverse(1 << N);S < (1 << N);++S)
			F[S] = Mult(F[S], invL);
}

int main()
{
