		gi(A[S]), SumA += A[S];
	for (int S = 0, inv = inverse(SumA);S < (1 << N);++S)
		A[S] = Mult(A[S], inv);
	FWT(A);
	E[0] = 0;
	for (int S = 1;S < (1 << N);++S)
		E[S] = inverse(Sub(A[S], 1));
	FWT(E, true);
	for (int S = 0, G1, _G1, H1, _H1, inv = inverse(1 << N);S < (1 << N);++S)
	{
		G1 = inv, _G1 = E[S], H1 = inv, _H1 = E[0];
		print(Mult(Sub(Mult(_G1, H1), Mult(G1, _H1)), inverse(squ(H1)))), putc('\n');
	}
	return 0;
}

