				ll x=v[j], y=v[j^i];
				v[j]=x+y, v[j^i]=x-y+MOD;
				if(v[j]>=MOD) v[j]-=MOD;
				if(v[j^i]>=MOD) v[j^i]-=MOD;
			}
		}
	}
}
