const int MOD = 998244353;

inline int fpm(int x, int y)
{
	int res = 1;
	for(; y; y >>= 1, x = (LL) x * x % MOD) if(y & 1) res = (LL) res * x % MOD;
	return res;
}

inline int inv(int x) { assert(x); return fpm(x, MOD - 2); }

const int MAXN = 18;

inline void FWT(int *f, int N, int type)
{
	for(int i = 0; i < N; ++i)
		for(int S = 0; S < 1 << N; ++S) if(!(S >> i & 1))
		{
			int X = f[S], Y = f[S | 1 << i];
			f[S] = (X + Y) % MOD;
			f[S | 1 << i] = (X - Y) % MOD;
		}

	if(type == -1)
	{
		int inv2N = inv(1 << N);
		for(int S = 0; S < 1 << N; ++S) f[S] = (LL) f[S] * inv2N % MOD;
	}
}

int n;

int g[1 << MAXN];

inline void input()
{
	n = read<int>();
	for(int S = 0; S < 1 << n; ++S) g[S] = read<int>();

	int sum = 0;
	for(int S = 0; S < 1 << n; ++S) (sum += g[S]) %= MOD;
	sum = inv(sum);
	for(int S = 0; S < 1 << n; ++S) g[S] = (LL) g[S] * sum % MOD;
}

inline void solve()
{
	static int f[1 << MAXN], h[1 << MAXN];

	(g[0] -= 1) %= MOD;

	for(int S = 0; S < 1 << n; ++S) h[S] = -1;
	(h[0] += 1 << n) %= MOD;

	FWT(h, n, +1);
	FWT(g, n, +1);

	for(int S = 1; S < 1 << n; ++S) f[S] = (LL) h[S] * inv(g[S]) % MOD;
	f[0] = 0;
	FWT(f, n, -1);

	for(int S = 1; S < 1 << n; ++S) (f[S] -= f[0]) %= MOD;
	f[0] = 0;

	for(int S = 0; S < 1 << n; ++S) printf("%d\n", (f[S] + MOD) % MOD);
}

