		{
			for (int l = 1; l < n; l <<= 1)
				for (int i = 0; i < n; i += (l << 1))
					for (int k = 0; k < l; k++)
					{
						int x = a[i + k], y = a[i + l + k];
						a[i + k] = (x + y) % P;
						a[i + l + k] = (x - y + P) % P;
					}
