inline lint inva(lint a){return powa(a,mo-2);}

const int inv2 = inva(2);

inline void fwtxor(lint a[],int nn,int dir)
{
	for(int i=1;i<nn;i<<=1)
		for(int j=0;j<nn;j+=i+i)
			for(int k=0;k<i;k++)
			{
				lint x=a[j+k],y=a[j+k+i];
				a[j+k]=(x+y)%mo,a[j+k+i]=(x-y+mo)%mo;
				if(dir<0)a[j+k]=a[j+k]*inv2%mo,a[j+k+i]=a[j+k+i]*inv2%mo;
			}
}

