#include <bits/stdc++.h>
using namespace std;

namespace TYC
{
	typedef long long ll;

	const int N = 1 << 18 | 5, mod = 998244353;

	int n, A[N], B[N];

	inline int read()
	{
		int x = 0, f = 0, ch = getchar();
		while (!isdigit(ch))
			f |= (ch == '-'), ch = getchar();
		while (isdigit(ch))
			x = x * 10 + ch - '0', ch = getchar();
		return f ? -x : x;
	}

	int inv(const int x) { return x == 1 ? 1 : int((ll)inv(mod % x) * (mod - mod / x) % mod); }

	void FWT(int *a)
	{
		for (int len = 1; len < n; len <<= 1)
			for (int i = 0; i < n; i += len << 1)
				for (int k = 0; k < len; k++)
				{
					int x = a[i + k], y = a[i + len + k];
					a[i + k] = (x + y) % mod;
					a[i + len + k] = (x - y + mod) % mod;
				}
	}

	void IFWT(int *a)
	{
		static const int INV2 = (mod + 1) >> 1;
		for (int len = 1; len < n; len <<= 1)
			for (int i = 0; i < n; i += len << 1)
				for (int k = 0; k < len; k++)
				{
					int x = a[i + k], y = a[i + len + k];
					a[i + k] = int((ll)(x + y) * INV2 % mod);
					a[i + len + k] = int((ll)(x - y + mod) * INV2 % mod);
				}
	}

	void work()
	{
		n = 1 << read();
		int sum = 0;
		for (int i = 0; i < n; i++)
			sum = (sum + (A[i] = read())) % mod;
		for (int i = 0, tmp = inv(sum); i < n; i++)
			A[i] = int((ll)A[i] * tmp % mod);
		A[0] = (A[0] - 1 + mod) % mod;
		for (int i = 0; i < n; i++)
			B[i] = mod - 1;
		B[0] = (n - 1 + mod) % mod;
		FWT(A), FWT(B);
		for (int i = 1; i < n; i++)
			A[i] = int((ll)B[i] * inv(A[i]) % mod);
		IFWT(A);
		for (int i = 0; i < n; i++)
			printf("%d\n", (A[i] - A[0] + mod) % mod);
	}
}

int main()
{
	TYC::work();
	return 0;
}

