	len = 1 << (n = read());
	Rep (i, len) (sum += p[i] = read()) %= Mod;
	sum = fpm(sum, Mod - 2);
	Rep (i, len) p[i] = 1ll * sum * p[i] % Mod; -- p[0];
	Rep (i, len) s[i] = (i ? 0 : len) - 1;

	FWT(p, 1); FWT(s, 1); Rep (i, len) p[i] = 1ll * s[i] * fpm(p[i], Mod - 2) % Mod; FWT(p, -1);

	Rep (i, len) printf ("%d\n", (p[i] - p[0] + Mod) % Mod);

	return 0;

}

