i64 pow(i64 a, i64 k) {
	i64 t = 1;
	for (; k; a = a * a % MOD, k >>= 1)
		if (k & 1) t = t * a % MOD;
	return t;
}

const int N = 1 << 18 | 7;

int n;
i64 p[N], s, q[N];

void fwt_xor(i64 f[], int n, int on) {
	for (int i = 0; i < n; ++i) {
		for (int j = 0; j < 1 << n; ++j)
			if (j >> i & 1)
				( f[j ^ 1 << i] += f[j] ) %= MOD, ( f[j] = f[j ^ 1 << i] - 2 * f[j] ) %= MOD;
		if (on == 1) continue;
		for (int i = 0; i < 1 << n; ++i)
			( f[i] *= Inv2 ) %= MOD;
	}
}

int main() {
	scanf("%d", &n);
	for (int i = 0; i < 1 << n; ++i)
		scanf("%lld", p + i), s += p[i];
	s = pow(s, MOD - 2);
	for (int i = 0; i < 1 << n; ++i)
		p[i] = p[i] * s % MOD;
	--p[0];
	std::fill(q, q + (1 << n), -1);
	q[0] += 1 << n;

	fwt_xor(p, n, 1), fwt_xor(q, n, 1);
	for (int i = 0; i < 1 << n; ++i)
		q[i] = q[i] * pow(p[i], MOD - 2) % MOD;
	fwt_xor(q, n, -1);

	for (int i = 0; i < 1 << n; ++i)
		printf("%lld\n", (q[i] - q[0] + MOD * 2) % MOD);
	return 0;
}

