#define ll long long
const int mod = 998244353;
using namespace std;
int sum(int a, int b){
    if (a+b<0) return a+b+mod;
    if (a+b >= mod) return a+b-mod;
    return a+b;
}
int mult(int a, int b){
    return ((ll) a * (ll) b) % (ll) mod;
}
int pw(int a, int b){
    if (b==0) return 1;
    if (b==1) return a;
    if (b % 2 != 0) return mult(a, pw(a, b-1));
    int R = pw(a, b/2);
    return mult(R, R);
}
int dv(int a, int b){
    return mult(a, pw(b, mod-2));
}
vector<int> solve(vector<int> a, vector<int> b){
    if (a.size() == 1){
        if (a[0] == 0) return {0};
        return {dv(b[0], a[0])};
    }
    vector<int> a1, b1;
    for (int i=0; i < a.size()/2; i++){
        a1.push_back(sum(a[2*i], a[2*i+1]));
        b1.push_back(sum(b[2*i], b[2*i+1]));
    }
    vector<int> F = solve(a1, b1);
    a1.clear(), b1.clear();
    for (int i=0; i < a.size()/2; i++){
        a1.push_back(sum(a[2*i], -a[2*i+1]));
        b1.push_back(sum(b[2*i], -b[2*i+1]));
    }
    vector<int> S = solve(a1, b1);
    vector<int> res;
    for (int i=0; i < a.size()/2;i++){
        res.push_back(dv(sum(F[i], S[i]), 2));
        res.push_back(sum(F[i], -res.back()));
    }
    return res;
}
signed main()
{
    int n;
    cin >> n;
    vector<int> v((1<<n));
    for (int i=0;i<(1<<n);i++) cin >> v[i];
    int S = 0;
    for (int i=0; i < (1<<n); i++) S += v[i];
    for (int i=0;i<(1<<n);i++) v[i] = dv(v[i], S);
    v[0] = sum(v[0], -1);
    vector<int> res = {sum(pw(2, n), -1)};
    for (int i=0; i < (1<<n)-1; i++) res.push_back(sum(0, -1));
    vector<int> ans = solve(v, res);
    for (int i=(1<<n)-1; i >= 0; i--){
        ans[i] = sum(ans[i], -ans[0]);
    }
    for (int i=0;i<(1<<n);i++) cout << ans[i] << "\n";
    return 0;
}

