Poly Fwt(Poly f) {
  int n = f.size();
  for (int x = 1; x < n; x <<= 1) {
    for (int s = 0; s < n; ++s) {
      if (s & x) continue;
      int t = f[s | x];
      f[s | x] = Sub(f[s] - t);
      Add(f[s], t);
    }
  }
  return f;
}

Poly Ifwt(Poly f) {
  int n = f.size();
  int inv = Inv(n);
  f = Fwt(f);
  for (int i = 0; i < n; ++i) {
    f[i] = Mul(f[i], inv);
  }
  return f;
}

