//the size of f must be a power of 2
template<class T>
void inplace_xor_transform(vector<T>&f){
	int n=f.size();
	for(int b=1;b<=n/2;b*=2){
		for(int i=0;i<n;i+=b*2){
			for(int j=i;j<i+b;j++){
				tie(f[j],f[j+b])=make_tuple(f[j]+f[j+b],f[j]-f[j+b]);
			}
		}
	}
}

