int difference(int a, int b) {
    return (a -= b) < 0 ? a += P : a;
}
int sum(int a, int b) {
    return (a += b) >= P ? a -= P : a;
}
int power(int a, int b) {
    int result = 1;
    while (b > 0) {
        if (b % 2 == 1)
            result = 1LL * result * a % P;
        a = 1LL * a * a % P;
        b /= 2;
    }
    return result;
}
void fwt(vector<int> &a) {
    for (int i = 1; i < a.size(); i *= 2) {
        for (int j = 0; j < a.size(); j += 2 * i) {
            for (int k = 0; k < i; ++k) {
                int u = sum(a[j + k], a[i + j + k]);
                int v = difference(a[j + k], a[i + j + k]);
                a[j + k] = u;
                a[i + j + k] = v;
            }
        }
    }
}
