	for (int i = 0; i < goal; i++)
		res[i] = 1ll * q[i] * power(p[i], P - 2) % P;
	UFWT(res, goal);
	for (int i = 0; i < goal; i++)
		writeln((res[i] + P) % P);
	return 0;
}

