void fwt(vector<int>& a, int flag) {
  int n = a.size();
  for (int h = 1; h < n; h <<= 1) {
    for (int step = h << 1, i = 0; i < n; i += step) {
      for (int j = i; j < i + h; ++j) {
        int x = a[j], y = a[j + h];
        if (flag > 0) {
          a[j] = (x + y) % MOD;
          a[j + h] = (x - y + MOD) % MOD;
        } else {
          a[j] = (x + y) * inv2 % MOD;
          a[j + h] = (x - y + MOD) * inv2 % MOD;
        }
      }
    }
  }
}

