void fwt(int *a, int n, int opt)
{
	for (int l = 2; l <= n; l *= 2)
	{
		int m = l / 2;
		for (int *p = a; p != a + n; p += l)
			for (int i = 0; i <= m - 1; i++)
			{
				int x = p[i], y = p[i + m];
				p[i] = ((ll)x + y) * INV % mod;
				p[i + m] = ((ll)x - y + mod) % mod * INV % mod;
			}
	}
}
int calc(int a, int k)
{
	int ans = 1;
	while (k)
	{
		if (k % 2) ans = (ll)ans * a % mod;
		a = (ll)a * a % mod;
		k /= 2;
	}
	return ans;
}
int main()
{
	int n, full;
	scanf("%d", &n);
	INV = calc(2, mod - 2);
	full = 1 << n;
	int sum = 0;
	for (int i = 0; i <= full - 1; i++)
	{
		scanf("%d", &p[i]);
		sum = (sum + p[i]) % mod;
	}
	sum = calc(sum, mod - 2);
	for (int i = 0; i <= full - 1; i++)
		p[i] = (ll)p[i] * sum % mod;
	p[0] = (p[0] - 1 + mod) % mod;
	x[0] = full - 1;
	for (int i = 1; i <= full - 1; i++)
		x[i] = mod - 1;
	fwt(x, full, 1);
	fwt(p, full, 1);
	for (int i = 0; i <= full - 1; i++)
		res[i] = (ll)x[i] * calc(p[i], mod - 2) % mod;
	fwt(res, full, -1);
	for (int i = 0; i <= full - 1; i++)
		printf("%d\n", (res[i] - res[0] + mod) % mod);
	return 0;
}

