void FST(vn& a, bool inv) {
	for (int n = sz(a), step = 1; step < n; step *= 2) {
		for (int i = 0; i < n; i += 2 * step) rep(j,i,i+step) {
			num &u = a[j], &v = a[j + step]; tie(u, v) =
				pii(u + v, u - v);                   // XOR
		}
	}
	if (inv) trav(x, a) x /= sz(a); // XOR only
}

