vector<ll> solve(const vector<ll> &a, const vector<ll> &b) {
    if (a.size() == 1) {
        return {a[0] ? b[0] * inv(a[0]) % MOD : 0};
    } else {
        vector<ll> al, ar, bl, br;
        for (int i = 0; i < a.size(); i += 2) {
