            bl.push_back((b[i] + b[i + 1]) % MOD);
            ar.push_back((a[i] - a[i + 1] + MOD) % MOD);
            br.push_back((b[i] - b[i + 1] + MOD) % MOD);
        }
        vector<ll> resl = solve(al, bl), resr = solve(ar, br), res;
        for (int i = 0; i < al.size(); i++) {
            res.push_back((resl[i] + resr[i]) * INV2 % MOD);
            res.push_back((resl[i] - resr[i] + MOD) * INV2 % MOD);
        }
