namespace Convolusion{
    /*
     A2^n

     R_k=sum_(i&j=k),A_i B_j
     R=mobius(zeta(A),zeta(B))


     R_k=sum_(i|j=k),A_i B_j
     R=mobius(zeta(A),zeta(B))

     */

    //R_i=sum_(j in i),A_j
    //if
    template<typename T>
    vector<T> zeta(const vector<T> &A,int n){
        vector<T> ret=A;
        for(int i=1;i<1<<n;i<<=1){
            for(int j=0;j<1<<n;j++){
                if((j&i)==0){ret[j|i]+=ret[j];}
            }
        }
        return ret;
    }

    //R_i=sum_(j in i),(-1)^(|i\j|) A_j
    //
    template<typename T>
    vector<T> mobius(const vector<T> &A,int n){
        vector<T> ret=A;
        for(int i=1;i<1<<n;i<<=1){
            for(int j=0;j<1<<n;j++){
                if((j&i)==0){ret[j|i]-=ret[j];}
            }
        }
        return ret;
    }

    //R_i=sum_(j),(-1)^(|i&j|) A_j
    //,sqrt(|A|)
    template<typename T>
    vector<T> hadamard(const vector<T> &A,int n){
        vector<T> ret=A;
        for(int i=1;i<1<<n;i<<=1){
            for(int j=0;j<1<<n;j++){
                if((i&j)==0){
                    T P=ret[j],Q=ret[j|i];
                    ret[j]=P+Q;
                    ret[j|i]=P-Q;
                }
            }
        }
        return ret;
    }

    //R_i=sum_(j),r^(ij) A_j
    //,sqrt(|A|),r1|A|
    template<typename T>
    vector<T> fourier(const vector<T> &A,int n,T r){
        vector<T> ret=A;
        vector<T> E(1<<n,1);
        for(int i=1;i<1<<n;i++){E[i]=E[i-1]*r;}
        for(int i=0,j=1;j<1<<n;j++){
            for(int k=1<<(n-1);k>(i^=k);k>>=1);
            if(i>j){swap(ret[i],ret[j]);}
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<1<<n;j+=2<<i){
                for(int k=0;k<1<<i;k++){
                    T P=ret[j|k],Q=ret[j|1<<i|k]*E[(1<<n-i-1)*k];
                    ret[j|k]=P+Q;
                    ret[j|1<<i|k]=P-Q;
                }
            }
        }
        return ret;
    }
};

template<long long int mod=1000000007>
struct Mod_Int{
    typedef long long int ll;
    typedef pair<ll,ll> pll;
    typedef Mod_Int<mod> M;
    ll a;

    ll mod_pow(ll a,ll x){
        a%=mod;
        ll ans=1;
        for(int i=0;i<63;i++){
            if(x>>i&1){ans*=a; ans%=mod;}
            a*=a;
            a%=mod;
        }
        return ans;
    }

    pll Ex_gcd(ll a,ll b){
        if(b==0){return {1,0};}
        pll ret=Ex_gcd(b,a%b);
        ret.F-=a/b*ret.S;
        return {ret.S,ret.F};
    }

    ll prime_R(ll a){
        return mod_pow(a,mod-2);
    }

    ll R(ll a){
        ll ret=Ex_gcd(a,mod).F;
        ret%=mod;
        if(ret<0){ret+=mod;}
        return ret;
    }

    Mod_Int(ll A=1):a(A){
        a%=mod;
        if(a<0){a+=mod;}
    }

    Mod_Int(const M &b):a(b.a){}

    M & operator += (const M &b){
        a+=b.a;
        if(a>=mod){a-=mod;}
        return *this;
    }

    M operator + (const M &b) const {
        M c=*this;
        return c+=b;
    }

    M & operator -= (const M &b){
        a-=b.a;
        if(a<0){a+=mod;}
        return *this;
    }

    M operator - (const M &b) const {
        M c=*this;
        return c-=b;
    }

    M & operator *= (const M &b){
        (a*=b.a)%=mod;
        return *this;
    }

    M operator * (const M &b) const {
        M c=*this;
        return c*=b;
    }

    M & operator /= (const M &b){
        (a*=R(b.a))%=mod;
        return *this;
    }

    M operator / (const M &b) const {
        M c=*this;
        return c/=b;
    }

    M & mod_pow_equal(ll x){
        ll ans=1;
        while(x>0){
            if(x&1){ans*=a; ans%=mod;}
            a*=a;
            a%=mod;
            x>>=1;
        }
        a=ans;
        return *this;
    }

    M mod_pow(ll x){
        M c(a);
        return c.mod_pow_equal(x);
    }

    bool operator == (const M &b) const {return a==b.a;}

    bool operator != (const M &b) const {return a!=b.a;}

    bool operator <= (const M &b) const {return a<=b.a;}

    bool operator < (const M &b) const {return a<b.a;}

    bool operator > (const M &b) const {return a>b.a;}

    bool operator >= (const M &b) const {return a>=b.a;}

    M & operator = (const M &b){
        a=b.a;
        return *this;
    }

    M & operator = (const ll &b){
        (a=b)%=mod;
        if(a<0){a+=mod;}
        return *this;
    }
};


