constexpr int MOD = 998244353;
inline int Sum(int x, int y) { return (x += y) >= MOD ? (x - MOD) : x; }
inline int Sub(int x, int y) { return (x -= y) < 0 ? (x + MOD) : x; }
inline int quick_pow(int a, int n)
{
	int res = 1;
	while (n)
	{
		if (n & 1)
			res = (ll)res * a % MOD;
		a = (ll)a * a % MOD;
		n >>= 1;
	}
	return res;
}
void solve(int *ans, int *arr, int *goal, int n)
{
	if (!n)
	{
		if (!arr[0])
			ans[0] = 0;
		else
			ans[0] = (ll)goal[0] * quick_pow(arr[0], MOD - 2) % MOD;
		return;
	}
	auto trans = [&] (int &x, int &y)
	{
		int a = x, b = y;
		x = Sum(a, b);
		y = Sub(a, b);
	};
	int len = 1 << n - 1;
	for (int i = 0; i < len; i++)
	{
		trans(arr[i], arr[i + len]);
		trans(goal[i], goal[i + len]);
	}
	solve(ans, arr, goal, n - 1);
	solve(ans + len, arr + len, goal + len, n - 1);
	for (int i = 0; i < len; i++)
		trans(ans[i], ans[i + len]);
	for (int i = 0; i < len << 1; i++)
		ans[i] = (ll)ans[i] * (MOD + 1) / 2 % MOD;
}
