	sum = quick_pow(sum, MOD - 2);
	for (int i = 0; i < 1 << n; i++)
		arr[i] = (ll)arr[i] * sum % MOD;
	arr[0] = Sub(arr[0], 1);
	for (int i = 0; i < 1 << n; i++)
		goal[i] = MOD - 1;
	goal[0] = Sum(goal[0], 1 << n);
	solve(ans, arr, goal, n);
	for (int i = 0; i < 1 << n; i++)
		printf("%d\n", Sub(ans[i], ans[0]));
