		while (!isdigit(ch))
			f |= (ch == '-'), ch = getchar();
		while (isdigit(ch))
			x = x * 10 + ch - '0', ch = getchar();
		return f ? -x : x;
	}

	int inv(const int x) { return x == 1 ? 1 : int((ll)inv(mod % x) * (mod - mod / x) % mod); }

	void FWT(int *a)
	{
		for (int len = 1; len < n; len <<= 1)
			for (int i = 0; i < n; i += len << 1)
				for (int k = 0; k < len; k++)
				{
					int x = a[i + k], y = a[i + len + k];
					a[i + k] = (x + y) % mod;
					a[i + len + k] = (x - y + mod) % mod;
				}
	}

	void IFWT(int *a)
	{
		static const int INV2 = (mod + 1) >> 1;
		for (int len = 1; len < n; len <<= 1)
			for (int i = 0; i < n; i += len << 1)
				for (int k = 0; k < len; k++)
				{
					int x = a[i + k], y = a[i + len + k];
					a[i + k] = int((ll)(x + y) * INV2 % mod);
					a[i + len + k] = int((ll)(x - y + mod) * INV2 % mod);
				}
	}

