		for (int i = 1; i < n; i++)
		{
			A[i] = i == 0 ? B[i] : int((ll)B[i] * inv(A[i]) % mod);
			sum = (sum + A[i]) % mod;
		}
		A[0] = sum;
		IFWT(A);
		for (int i = 0; i < n; i++)
			printf("%d\n", (A[i] - A[0] + mod) % mod);
	}
