    M operator + (const M &b) const {
        M c=*this;
        return c+=b;
    }

    M & operator -= (const M &b){
        a-=b.a;
        if(a<0){a+=mod;}
        return *this;
    }

    M operator - (const M &b) const {
        M c=*this;
        return c-=b;
    }

    M & operator *= (const M &b){
        (a*=b.a)%=mod;
        return *this;
    }

    M operator * (const M &b) const {
        M c=*this;
        return c*=b;
    }

    M & operator /= (const M &b){
        (a*=R(b.a))%=mod;
        return *this;
    }

    M operator / (const M &b) const {
        M c=*this;
        return c/=b;
    }

    M & mod_pow_equal(ll x){
        ll ans=1;
        while(x>0){
            if(x&1){ans*=a; ans%=mod;}
            a*=a;
            a%=mod;
            x>>=1;
        }
        a=ans;
        return *this;
    }

    M mod_pow(ll x){
        M c(a);
        return c.mod_pow_equal(x);
    }

    bool operator == (const M &b) const {return a==b.a;}

    bool operator != (const M &b) const {return a!=b.a;}

    bool operator <= (const M &b) const {return a<=b.a;}

    bool operator < (const M &b) const {return a<b.a;}

    bool operator > (const M &b) const {return a>b.a;}

    bool operator >= (const M &b) const {return a>=b.a;}

    M & operator = (const M &b){
        a=b.a;
        return *this;
    }

    M & operator = (const ll &b){
        (a=b)%=mod;
        if(a<0){a+=mod;}
        return *this;
    }
};


