In void calc(ll &x,ll &y,int opt){
	if(opt == 1){
		ll X = Add(x,y),Y = Sub(x,y);
		x = X,y = Y;
	}
	else{
		ll X = Add(x,y) * iv2 % mod,Y = Sub(x,y) * iv2 % mod; 
		x = X,y = Y;
	}
}

