using namespace std;

typedef long long ll;

ll pow_mod(ll x,int k) {
  ll ans=1;
  while (k) {
  	if (k&1) ans=ans*x%MOD;
  	x=x*x%MOD;
  	k>>=1;
  }
  return ans;
}

void fwt(int *p,int len) {
  for(int i=1;i<len;i<<=1)
    for(int j=0;j<len;j++)
      if (j&i) {
      	int u=p[j^i],v=p[j];
      	p[j^i]=(u+v)%MOD;
      	p[j]=(u-v+MOD)%MOD;
	  }
}

int p[1<<18],q[1<<18],ans[1<<18];
ll l[(1<<18)+1],r[(1<<18)+1];

void solve(int n) {
  fwt(p,1<<n);
  l[0]=r[(1<<n)]=1;
  for(int i=1;i<(1<<n);i++) l[i]=l[i-1]*(1LL-p[i]+MOD)%MOD;
  for(int i=(1<<n)-1;i>0;i--) r[i]=r[i+1]*(1LL-p[i]+MOD)%MOD;
  for(int i=1;i<(1<<n);i++) {
  	ll s=l[i-1]*r[i+1]%MOD;
  	q[0]=(q[0]-p[i]*s%MOD+MOD)%MOD;
  	q[i]=(q[i]-p[0]*s%MOD+MOD)%MOD;
  }
  ll s=0;
  for(int i=0;i<(1<<n);i++) s=(s+q[i])%MOD;
  fwt(q,1<<n);
  ll inv=pow_mod((ll)r[1]*r[1]%MOD,MOD-2);
  for(int i=0;i<(1<<n);i++) ans[i]=r[1]*(q[i]-s+MOD)%MOD*inv%MOD;
}

