const int p = 998244353;
inline int qpow(int value, int n) {
  int result = 1;
  for (; n; n >>= 1, value = (ll)value * value % p) {
    if (n & 1) {
      result = (ll)result * value % p;
    }
  }
  return result;
}
void fwt(vector<int> &arr) {
  for (int gap = 1; gap < arr.size(); gap <<= 1) {
    for (int start = 0; start < arr.size(); start += gap * 2) {
      for (int i = start; i < start + gap; i++) {
        int x = arr[i], y = arr[i + gap];
        arr[i] = (x + y) % p;
        arr[i + gap] = (x - y + p) % p;
      }
    }
  }
}
