ll qpow(ll x, ll t)
{
	ll ans = 1;
	while(t)
	{
		if(t & 1) ans = ans * x % MOD;
		x = x * x % MOD;
		t >>= 1;
	}
	return ans;
}

ll inv(ll x)
{
	return qpow(x, MOD-2);
}

void fwt(ll *f, int bit)
{
	for(int i=1; i<mov(bit); i<<=1)
		for(int j=0; j<mov(bit); j+=(i<<1))
			for(int k=0; k<i; k++)
			{
				ll x = f[j+k], y = f[j+k+i];
				f[j+k] = (x+y) % MOD;
				f[j+k+i] = (x-y+MOD) % MOD;
			}
}

