}

void fwt(int* a, int n) {
  for (int i = 0; i < n; ++i)
    for (int j = 0; j < 1 << n; ++j)
      if ((j >> i) & 1) {
        int a0 = norm(a[j ^ (1 << i)] + a[j]);
        a[j] = norm(P + a[j ^ (1 << i)] - a[j]);
        a[j ^ (1 << i)] = a0;
      }
}

