#define ll long long
using namespace std;
const ll MOD=998244353,inv2=499122177;
int n;
ll p[1<<18],a[1<<18],b[1<<18],c[1<<18];
ll quick_pow(ll x,ll a)
{
	ll ans=1;
	while(a)
	{
		if(a&1)ans=ans*x%MOD;
		x=x*x%MOD;
		a>>=1;
	}
	return ans;
}
void solve(int n,ll*p,ll*a,ll*b,ll*c,int t)
{
	if(n==1)
	{
		ll inv=quick_pow(1+MOD-p[0],MOD-2);
		a[1]=inv*p[1]%MOD;
		c[1]=inv*t%MOD;
		a[0]=1;
		return;
	}
	int m=1<<(n-1);
	for(int i=0;i<m;i++)
	{
		p[i]=(p[i]+p[i+m])%MOD;
		p[i+m]=(p[i]+2*MOD-2*p[i+m])%MOD;
	}
	solve(n-1,p,a,b,c,t);
	solve(n-1,p+m,a+m,b+m,c+m,0);
	for(int i=0;i<m;i++)
	{
		a[i]=b[i]=a[i]*inv2%MOD;
		a[i+m]=a[i+m]*inv2%MOD,b[i+m]=MOD-a[i+m];
		a[i]+=a[i+m],a[i+m]=a[i]+MOD-2*a[i+m];
		b[i]+=b[i+m],b[i+m]=b[i]+MOD-2*b[i+m];
		c[i]+=c[i+m],c[i+m]=c[i]+MOD-2*c[i+m];
		ll p1=(p[i]+p[i+m])*inv2%MOD;
		ll p2=(p[i]-p[i+m]+MOD)*inv2%MOD;
		p[i]=p1,p[i+m]=p2;
	}
	for(int i=0;i<(1<<n);i++)
	{
		a[m]=(a[m]+p[i]*a[m^i])%MOD;
		b[m]=(b[m]+p[i]*b[m^i])%MOD;
		c[m]=(c[m]+p[i]*c[m^i])%MOD;
	}
	b[m]--;
	ll inv=quick_pow(1+MOD-b[m],MOD-2);
	a[m]=a[m]*inv%MOD;
	b[m]=0;
	c[m]=(c[m]+t)*inv%MOD;
	for(int i=1;i<(1<<n);i++)
	{
		a[i]=(a[i]+b[i]*a[m])%MOD;
		c[i]=(c[i]+b[i]*c[m])%MOD;
		b[i]=0;
	}
}
int main()
{
	scanf("%d",&n);
	ll s=0;
	for(int i=0;i<(1<<n);i++)
	{
		scanf("%lld",&p[i]);
		s+=p[i];
	}
	s=quick_pow(s,MOD-2);
	for(int i=0;i<(1<<n);i++)p[i]=p[i]*s%MOD;
	solve(n,p,a,b,c,1);
	for(int i=0;i<(1<<n);i++)printf("%lld\n",c[i]);
	return 0;
}

