void FWT(int *a, int n) {
	for(int i = 1; i < n; i <<= 1) {
		for(int j = 0, p = i << 1; j < n; j += p) {
			int *u = a + j;
			int *v = a + j + i;
			for(int k = 0; k < i; ++k, ++u, ++v) {
				int x = *u;
				int y = *v;
				*u = add(x + y);
				*v = dec(x - y);
			}
		}
	}
}
void IFWT(int *a, int n) {
	for(int i = 1; i < n; i <<= 1) {
		for(int j = 0, p = i << 1; j < n; j += p) {
			int *u = a + j;
			int *v = a + j + i;
			for(int k = 0; k < i; ++k, ++u, ++v) {
				int x = *u;
				int y = *v;
				*u = (ll)(x + y) * r2 % mod;
				*v = (ll)(x - y) * r2 % mod; *v = dec(*v);
			}
		}
	}
}
void init() {
	r2 = inver(2);
	int sum = 0;
	for(int i = 0; i < n; ++i) {
		sum += p[i];
	}
	sum = inver(sum);
	for(int i = 0; i < n; ++i) {
		p[i] = (ll)p[i] * sum % mod;
	}
}
void solve() {
	init();
	p[0] = dec(p[0] - 1);
	a[0] = n - 1;
	for(int i = 1; i < n; ++i) {
		a[i] = mod - 1;
	}
	FWT(a, n), FWT(p, n);
	for(int i = 0; i < n; ++i) {
		a[i] = a[i] * inver(p[i]) % mod;
	}
	IFWT(a, n);
	ll rec = a[0];
	FWT(a, n);
	a[0] = (ll)(mod - rec) * n % mod;
	IFWT(a, n);
}
