void fft(int n,int *f)  {
    for(int k = 1 ; k < n ; k <<= 1)
    for(int i = 0 ; i < n ; i += 2 * k)
    for(int j = 0 ; j < k ; ++j)    {
        int x = f[i + j];
        int y = f[i + j + k];

        f[i + j]     = (x + y) % mod;
        f[i + j + k] = (x - y + mod) % mod;
    }
}

