int power(int a, int x) {
	int ans = 1;
	for (; x; x >>= 1, a = 1ll * a * a % P)
		if (x & 1) ans = 1ll * ans * a % P;
	return ans;
}

void fwt(int *A) {
	for (int d = 1; d < 1 << n; d <<= 1)
		for (int i = 0; i < 1 << n; i += d << 1)
			for (int j = 0; j < d; ++j) {
				int a = A[i + j], b = A[i + d + j];
				A[i + j] = (a + b) % P;
				A[i + d + j] = (a + P - b) % P;
			}
}

int main() {
	scanf("%d", &n);
	int sum = 0;
	for (int i = 0; i < 1 << n; ++i)
		scanf("%d", A + i), sum += A[i];
	sum = power(sum, P - 2);
	for (int i = 0; i < 1 << n; ++i)
		A[i] = 1ll * A[i] * sum % P;
	fwt(A);
	A[0] = 0;
	for (int i = 1; i < 1 << n; ++i)
		A[i] = power(A[i] + P - 1, P - 2);
	fwt(A);
	for (int i = 0; i < 1 << n; ++i)
		printf("%d\n", (A[i] + P - A[0]) % P);
}

