inline int Sum(int x, int y) { return (x += y) >= MOD ? (x - MOD) : x; }
inline int Sub(int x, int y) { return (x -= y) < 0 ? (x + MOD) : x; }
inline int quick_pow(int a, int n)
{
	int res = 1;
	while (n)
	{
		if (n & 1)
			res = (ll)res * a % MOD;
		a = (ll)a * a % MOD;
		n >>= 1;
	}
	return res;
}
inline void FWT(int *arr, int n, bool flg)
{
	int coef = flg ? (MOD + 1) / 2 : 1;
	for (int len = 1; len < n; len <<= 1)
	{
		for (int i = 0; i < n; i += len << 1)
		{
			for (int j = i; j < i + len; j++)
			{
				int x = arr[j], y = arr[j + len];
				arr[j] = (ll)(x + y) * coef % MOD;
				arr[j + len] = (ll)(x - y + MOD) * coef % MOD;
			}
		}
	}
}
