		long[] hx = new long[1<<n];
		for(int i = 0;i < 1<<n;i++){
			hx[i] = hb[i] * invl(ha[i], mod) % mod;
		}
		long[] x = fwht(hx, mod);

		long u = invl(1<<n, mod);
